'use strict';

const Joi = require('joi');
const buildSchema = require('./../build-schema');
const common = require('../common');

const baseFields = {
  id: common.id,
  device_id: Joi.string().max(100),
  user_id: common.id,
  name: common.name,
  slug: common.slug,
  description: common.description,
  type: Joi.string().max(255),
  pin: Joi.string().max(255),
  otp_secret: Joi.string().max(255),
  active: common.active
};

const requiredForPost = [];

const attributeFields = {};

const additionalForbiddenAttribs = ['passcode'];

module.exports = buildSchema(baseFields, requiredForPost, attributeFields, additionalForbiddenAttribs);
