'use strict';

const Joi = require('joi');
const buildSchema = require('./../build-schema');
const common = require('../common');

const baseFields = {
  id: common.id,
  type: Joi.string().max(45),
  name: Joi.string().max(45),
  category: Joi.string().max(45),
  body: Joi.string().max(255),
  whitelabel: common.whitelabel,
  version: Joi.string().max(45),
  lang: common.lang,
  active: common.active
};

const requiredForPost = ['type', 'name', 'category', 'body', 'whitelabel', 'version', 'lang'];

const attributeFields = {};

const additionalForbiddenAttribs = [];

module.exports = buildSchema(baseFields, requiredForPost, attributeFields, additionalForbiddenAttribs);
