'use strict';

const Joi = require('joi');
const buildSchema = require('./../build-schema');
const common = require('../common');

var baseFields = {
  id: common.id,
  name: common.name,
  val: Joi.string(),
  lang: common.lang,
  region: common.region,
  active: common.active,
  translated: Joi.number().integer().max(1).description('translated flag which is represented as a tinyint -- only possible values should be 0 or 1')

};

const requiredForPost = [];

const attributeFields = {};

const additionalForbiddenAttribs = [];

module.exports = buildSchema(baseFields, requiredForPost, attributeFields, additionalForbiddenAttribs);
