'use strict';

const Joi = require('joi');
const buildSchema = require('./../build-schema');
const common = require('../common');

const baseFields = {
  id: common.id,
  user_id: Joi.number().integer().positive(),
  policy_id: Joi.number().integer().positive(),
  from_ip: Joi.string().max(255),
  from_browser: Joi.string().max(255),
  date_accepted: Joi.date().iso()
};

const requiredForPost = ['user_id', 'policy_id', 'from_ip', 'from_browser', 'date_accepted'];

const attributeFields = {};

const additionalForbiddenAttribs = [];

module.exports = buildSchema(baseFields, requiredForPost, attributeFields, additionalForbiddenAttribs);
