'use strict';

const _ = require('lodash');

function extractUtmParamsFromUser (user) {
  return {
    source: _.get(user, 'referral.source'),
    medium: _.get(user, 'referral.medium'),
    term: _.get(user, 'referral.term'),
    content: _.get(user, 'referral.content'),
    name: _.get(user, 'referral.name'),
    baId: _.get(user, 'referral.baid') || _.get(user, 'referral.baId'),
    subId1: _.get(user, 'referral.subid1') || _.get(user, 'referral.subId1'),
    subId2: _.get(user, 'referral.subid2') || _.get(user, 'referral.subId2'),
    subId3: _.get(user, 'referral.subid3') || _.get(user, 'referral.subId3'),
    subId4: _.get(user, 'referral.subid4') || _.get(user, 'referral.subId4'),
    linkId: _.get(user, 'referral.linkid') || _.get(user, 'referral.linkId')
  };
}

module.exports = extractUtmParamsFromUser;
